import React from "react";

import Axios from "axios";
import { useState } from "react";

function App() {
  const [payeename, setPayeeName] = useState("");
  const [upiid, setUpiId] = useState(0);
  const [nickname, setNickname] = useState("");
  const [amount, setAmount] = useState(0);
  const [cupiid, setCUpiId] = useState("");
  const [mobilenumber, setMobileNumber] = useState(0);
  const [address, setAddress] = useState("");
  const [comments, setComments] = useState("");

  const Add = () => {
    Axios.post("http://localhost:3001/create", {
      nickname: nickname,
      payeename: payeename,
      upiid: upiid,
      cupiid: cupiid,
      amount: amount,
      mobilenumber: mobilenumber,
      address: address,
      comments: comments,
    }).then(() => {
      console.log("Success!");
    });
  };

  return (
    <div className="UpiBanking">
      <div className="information">
        <h4>Commerce UPI Payment</h4>
        <label>Nickname </label>
        <input type="text" onChange={(e) => setNickname(e.target.value)} />
        <label>PayeeName </label>
        <input type="text" onChange={(e) => setPayeeName(e.target.value)} />
        <label>UpiId </label>
        <input type="number" onChange={(e) => setUpiId(e.target.value)} />
        <label>CUpiId </label>
        <input type="number" onChange={(e) => setCUpiId(e.target.value)} />
        <label>Amount</label>
        <input type="number" onChange={(e) => setAmount(e.target.value)} />
        <label>MobileNumber </label>
        <input
          type="number"
          onChange={(e) => setMobileNumber(e.target.value)}
        />
        <label>Address</label>
        <input type="text" onChange={(e) => setAddress(e.target.value)} />
        <label>Comments </label>
        <input type="text" onChange={(e) => setComments(e.target.value)} />
        <button>Sign in</button>
        <button onClick={Add}>Add</button>
      </div>
    </div>
  );
}
export default App;
